import React from 'react'
import { Document, Page } from 'react-pdf/dist/esm/entry.webpack5';
import {useState} from "react";

const Blog = ({blog}) => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }
 console.log(blog.document_url)

  return (
    <div>
        <h1>{blog.title}</h1>
        <div><a href={blog.document_url} target="_blank"><img src={blog.picture_url}/></a></div>
        <div>Click Picture to View PDF of Blog in New Tab</div>
          <div>
              <Document file={blog.document_url} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pageNumber} />
              </Document>
              <p>
                Page {pageNumber} of {numPages}
              </p>
         </div>




    </div>

  )
}

export default Blog
