import React from 'react'
import {useState} from "react";
import { useNavigate } from 'react-router-dom';


const NewBlog = ({blogs, setBlogs}) => {
    const [title, setTitle] = useState("")
    const [picture, setPicture] = useState("")


    let navigate = useNavigate()


    function handleSubmit(e){
        e.preventDefault()
        const formData = {
            title,
            picture
        }
        const configObj = {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify( formData ),
          }


        if (title && picture){

            console.log("got in here")
            fetch("/blogs", configObj)
            .then(r=>r.json())
            .then(newBlog => setBlogs([...blogs, newBlog]))
        }
        console.log("submitted!")
        navigate("/blogs")
    }
  return (
    <div>NewBlog
        <form>
            Title:
            <input onChange={(e)=>setTitle(e.target.value)} value={title} placeholder="stuff" name="title" id="title"/>
            Picture:
            <input onChange={(e)=>setPicture(e.target.value)} type="file" id="picture" name="picture" accept="image/png, image/jpeg"/>
            <input type="submit" onSubmit={handleSubmit}/>
        </form>

    </div>
  )
}

export default NewBlog
