import React from 'react'
import {useState} from "react";


const NewBlog = ({blogs, setBlogs}) => {
    const [title, setTitle] = useState("")
    const [picture, setPicture] = useState("")

    console.log(title)
    console.log(picture)

    function handleSubmit(e){
        e.preventDefault()
        if (title && picture){
            const configObj = {
                title,
                picture
            }
            fetch("/blogs", configObj)
            .then(r=>r.json())
            .then(newBlog => setBlogs([...blogs, newBlog]))
        }
    }
  return (
    <div>NewBlog
        <form onSubmit={handleSubmit}>
            Title:
            <input onChange={(e)=>setTitle(e.target.value)} value={title} placeholder="stuff" name="title" id="title"/>
            Picture:
            <input onChange={(e)=>setPicture(e.target.value)} type="file" id="picture" name="picture" accept="image/png, image/jpeg"/>
            <input type="submit"/>
        </form>

    </div>
  )
}

export default NewBlog
